import React, { useState, useEffect } from "react";
import { teamsService } from "../services";
import { TeamAppBar, TeamsList, Roster } from "../components";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";

const drawerWidth = 240;

const Dashboard = () => {
  const [teams, setTeams] = useState();
  const [selectedTeamIndex, setSelectedTeamIndex] = useState(0);
  const [selectedPlayerId, setSelectedPlayerId] = useState();

  useEffect(() => {
    if (!teams) {
      getTeams();
    }
  });

  const getTeams = async () => {
    let res = await teamsService.getTeams();
    setTeams(
      // sort teams by name
      res.sort((firstEl, secondEl) => {
        return firstEl.name.localeCompare(secondEl.name);
      })
    );
  };

  const handleTeamsListItemClick = (event, index) => {
    setSelectedTeamIndex(index);
  };

  const handlePlayerListItemClick = (event, index) => {
    setSelectedPlayerId(index);
  };

  return (
    <React.Fragment>
      <TeamAppBar
        team={teams && teams[selectedTeamIndex]}
        drawerWidth={drawerWidth}
      />
      <TeamsList
        teams={teams}
        drawerWidth={drawerWidth}
        handleTeamsListItemClick={handleTeamsListItemClick}
        selectedTeamIndex={selectedTeamIndex}
      />
      <Box
        component="main"
        sx={{ flexGrow: 1, bgcolor: "background.default", p: 3 }}
      >
        <Toolbar />
        <Roster team={teams && teams[selectedTeamIndex]} />
      </Box>
    </React.Fragment>
  );
};

export default Dashboard;
